import java.util.*;
public class CalculateAverage {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		int b = sc.nextInt();
		int c = sc.nextInt();
		int average = (a + b + c) / 3;
		System.out.println(average);
	}

}
